package gov.nih.nci.caintegrator.service.task;

import gov.nih.nci.caintegrator.dto.query.QueryDTO;
import gov.nih.nci.caintegrator.enumeration.FindingStatus;

import java.io.Serializable;

/**
 * Task is the object used to identify TaskResult objects,
 * which contain all information relating to a query and its
 * corresponding results. The Task is identified by an id
 * generated by the user , usually, and a cacheId which is a key
 * to find the object in the persistent store.
 * @author rossok
 *
 */

public class Task implements Serializable{
    
    private static final long serialVersionUID = -1301876517502792305L;
    private String id;
    private String cacheId;
    private FindingStatus status;
    private QueryDTO queryDTO;
    private long elapsedTime;
    private long endTime;
    private long startTime;
    
    public Task(String id, String cacheId, FindingStatus status, QueryDTO queryDTO){
        this.id = id;
        this.cacheId = cacheId;
        this.status = status;
        this.queryDTO = queryDTO;
    }
    
    /**
     * @return Returns the cacheId.
     */
    public String getCacheId() {
        return cacheId;
    }
    /**
     * @param cacheId The cacheId to set.
     */
    public void setCacheId(String cacheId) {
        this.cacheId = cacheId;
    }
    /**
     * @return Returns the id.
     */
    public String getId() {
        return id;
    }
    /**
     * @param id The id to set.
     */
    public void setId(String id) {
        this.id = id;
    }
    /**
     * @return Returns the status.
     */
    public FindingStatus getStatus() {
        return status;
    }
    /**
     * @param status The status to set.
     */
    public void setStatus(FindingStatus status) {
        this.status = status;
    }

    /**
     * @return Returns the queryDTO.
     */
    public QueryDTO getQueryDTO() {
        return queryDTO;
    }

    /**
     * @param queryDTO The queryDTO to set.
     */
    public void setQueryDTO(QueryDTO queryDTO) {
        this.queryDTO = queryDTO;
    }   
    
    /**
     * @return Returns the elapsedTime.
     */
    public long getElapsedTime() {
        return elapsedTime;
    }


    /**
     * @param elapsedTime The elapsedTime to set.
     */
    public void setElapsedTime(long elapsedTime) {
        this.elapsedTime = elapsedTime;
    }


    /**
     * @return Returns the endTime.
     */
    public long getEndTime() {
        return endTime;
    }


    /**
     * @param endTime The endTime to set.
     */
    public void setEndTime(long endTime) {
        this.endTime = endTime;
    }

    
    /**
     * @return Returns the startTime.
     */
    public long getStartTime() {
        return startTime;
    }


    /**
     * @param startTime The startTime to set.
     */
    public void setStartTime(long startTime) {
        this.startTime = startTime;
    }

}
